oat: right;\r\n        line-height: $base-line-height * $base-font-size * 2.25;\r\n        .nav-trigger {\r\n            display: none;\r\n        }\r\n        .menu-icon {\r\n            display: none;\r\n        }\r\n        .page-link {\r\n            font-size: 14px;\r\n            color: $text-color;\r\n            line-height: $base-line-height;\r\n            text-align: left; // Gaps between nav items, but not on the last one\r\n            // text-transform: uppercase; 是否需要大写\r\n            text-decoration: none;\r\n            &:hover {\r\n                color: $main-color;\r\n            }\r\n            &:not(:last-child) {\r\n                margin-right: 15px;\r\n            }\r\n        }\r\n        @include media-query($on-palm) {\r\n            position: absolute;\r\n            top: 9px;\r\n            right: $spacing-unit / 2;\r\n            background-color: $background-color;\r\n            border: 1px solid $grey-color-light;\r\n            border-radius: 5px;\r\n            text-align: right;\r\n            label[for=\"nav-trigger\"] {\r\n                display: block;\r\n                float: right;\r\n                width: 36px;\r\n                height: 36px;\r\n                z-index: 2;\r\n                cursor: pointer;\r\n            }\r\n            .menu-icon {\r\n                display: block;\r\n                float: right;\r\n                width: 36px;\r\n                height: 26px;\r\n                line-height: 0;\r\n                padding-top: 10px;\r\n                text-align: center;\r\n                >svg path {\r\n                    fill: $grey-color-dark;\r\n                }\r\n            }\r\n            input~.trigger {\r\n                clear: both;\r\n                display: none;\r\n            }\r\n            input:checked~.trigger {\r\n                display: block;\r\n                padding-top: 5px;\r\n                padding-bottom: 5px;\r\n            }\r\n            .page-link {\r\n                display: block;\r\n                padding: 5px 20px;\r\n                font-size: 16px;\r\n                &:not(:last-child) {\r\n                    margin-right: 0;\r\n                }\r\n            }\r\n        }\r\n    }\r\n}\r\n\r\n.today-logion-container {\r\n    .today-logion {\r\n        margin: 15px auto 15px;\r\n        color: #999;\r\n        font-size: 13px;\r\n    }\r\n}","/**\r\n* comments\r\n*/\r\n\r\n.site-comments {\r\n    .comments-cue {\r\n        border-top: 1px solid $ddd-color;\r\n        border-bottom: 1px solid $ddd-color;\r\n        padding: 10px 0;\r\n        font-size: 10px;\r\n        color: $assistant-color;\r\n        font-style: oblique;\r\n        span {\r\n            color: $main-color;\r\n        }\r\n    }\r\n    .comment,\r\n    .hc__right {\r\n        padding: 10px !important;\r\n    }\r\n}","/**\r\n* contact-info\r\n*/\r\n\r\n.site-contact-info {\r\n    background: $main-background-color-one;\r\n    border-top: 1px solid rgba(230, 230, 230, 0.99);\r\n    .contact-info-content {\r\n        padding: 20px 10px;\r\n        font-size: 14px;\r\n        div {\r\n            margin-left: 30px;\r\n            margin-right: 30px;\r\n            font-size: 16px;\r\n            height: 25px;\r\n            line-height: 25px;\r\n            clear: both;\r\n            a {\r\n                color: $main-color;\r\n            }\r\n            .about {\r\n                float: left;\r\n            }\r\n            .comment {\r\n                float: right;\r\n                font-size: 10px;\r\n                -webkit-transform: scale(0.8);\r\n                -moz-transform: scale(0.8);\r\n                -o-transform: scale(0.8);\r\n                transform: scale(0.8);\r\n            }\r\n        }\r\n    }\r\n    ul {\r\n        list-style: none;\r\n        padding-left: 0;\r\n        clear: both;\r\n        li {\r\n            float: left;\r\n            width: 130px;\r\n            padding-left: 30px;\r\n            margin-top: 15px;\r\n            dt {\r\n                font-size: 16px;\r\n                color: $text-color;\r\n                line-height: 22px;\r\n                margin-bottom: 5px;\r\n            }\r\n            dd {\r\n                line-height: 25px;\r\n                color: $link-color;\r\n                a {\r\n                    color: $link-color;\r\n                    &:hover {\r\n                        color: $main-color;\r\n                    }\r\n                }\r\n            }\r\n        }\r\n    }\r\n}","/**\r\n* site-footer\r\n*/\r\n\r\n.site-footer {\r\n    background-color: $text-color;\r\n    color: $white-color;\r\n    line-height: 25px;\r\n    a {\r\n        color: $white-color;\r\n    }\r\n    .license-content {\r\n        max-width: 880px;\r\n        padding-top: 20px;\r\n        img {\r\n            margin-left: 20px;\r\n            height: 25px;\r\n        }\r\n        p {\r\n            padding: 0 10px;\r\n            font-size: 12px;\r\n            text-align: center;\r\n            line-height: 1.5;\r\n        }\r\n    }\r\n    .copyright-content {\r\n        padding: 20px 0 40px;\r\n        p {\r\n            text-align: center;\r\n        }\r\n        a {\r\n            color: $white-color;\r\n        }\r\n        .theme {\r\n            font-size: 14px;\r\n            line-height: 20px;\r\n            .author {\r\n                color: $main-color;\r\n                font-weight: 900;\r\n            }\r\n        }\r\n    }\r\n}","/**\r\n * page-list\r\n */\r\n\r\n.site-page-list {\r\n    legend {\r\n        color: $main-color;\r\n        width: 100%;\r\n        padding: 0;\r\n        padding-bottom: 3px;\r\n        margin-top: 5px;\r\n        margin-bottom: 10px;\r\n        font-size: 21px;\r\n        line-height: inherit;\r\n        border: 0;\r\n        border-bottom: 1px solid $main-background-color-one;\r\n        border-bottom: 1px solid rgba(230, 230, 230, 0.99);\r\n    }\r\n    h4 {\r\n        padding-left: .7em;\r\n        font-size: 17px;\r\n        font-weight: 900;\r\n        color: #444555;\r\n    }\r\n    .list {\r\n        list-style: none;\r\n        padding-left: 5px;\r\n        li {\r\n            padding: .3rem 1.3rem;\r\n            a {\r\n                font-family: inherit;\r\n                font-weight: 500;\r\n                line-height: 1.5;\r\n                color: inherit;\r\n                text-decoration: none;\r\n                &:hover {\r\n                    color: $main-color;\r\n                }\r\n            }\r\n        }\r\n    }\r\n}","/**\r\n * home\r\n */\r\n\r\n.home {\r\n    box-sizing: border-box;\r\n    padding-top: 30px;\r\n    padding-bottom: 50px;\r\n    width: 100%;\r\n    h3 {\r\n        /* text-align: center; */\r\n        font-size: 30px;\r\n        line-height: 60px;\r\n    }\r\n    .info {\r\n        padding-top: 40px;\r\n    }\r\n    .info a {\r\n        text-decoration: none;\r\n    }\r\n    .btn {\r\n        display: inline-block;\r\n        height: 38px;\r\n        width: 120px;\r\n        border: 1px solid $white-color;\r\n        border-radius: 19px;\r\n        color: $white-color;\r\n        line-height: 38px;\r\n        letter-spacing: 0.5px;\r\n        text-align: center;\r\n        font-weight: normal;\r\n        margin-right: 10px;\r\n    }\r\n    .btn-primary {\r\n        background: $main-color-two;\r\n        border-color: $main-color-two;\r\n        &:hover {\r\n            background: $mian-hover-color-two;\r\n        }\r\n    }\r\n    .btn-secondary {\r\n        border: 1px solid $main-color-two;\r\n        border-radius: 100px;\r\n        color: $main-color-two;\r\n        &:hover {\r\n            background: $mian-hover-color-two;\r\n            color: $white-color;\r\n        }\r\n    }\r\n}","/**\r\n * index\r\n */\r\n\r\n.index {\r\n    hr {\r\n        border: 0;\r\n        border-top: 1px solid rgba(230, 230, 230, 0.99);\r\n    }\r\n    .post-preview {\r\n        >a {\r\n            &:hover,\r\n            &:focus {\r\n                text-decoration: none;\r\n                .post-title {\r\n                    color: $main-color;\r\n                }\r\n                .post-content-preview {\r\n                    color: $main-color;\r\n                }\r\n                >.post-meta {\r\n                    color: #a3a3a3;\r\n                }\r\n            }\r\n            >.post-title {\r\n                font-size: 24px;\r\n                line-height: 1.3;\r\n                margin-top: 15px;\r\n                margin-bottom: 10px;\r\n                color: $text-color;\r\n            }\r\n            >.post-subtitle {\r\n                font-size: 15px;\r\n                line-height: 1.3;\r\n                margin: 0;\r\n                font-weight: 300;\r\n                margin-bottom: 10px;\r\n            }\r\n            >.post-meta {\r\n                font-size: 12px;\r\n                font-style: italic;\r\n                margin-top: 8px;\r\n                margin-bottom: 10px;\r\n                text-align: right;\r\n                padding-right: 20px;\r\n            }\r\n        }\r\n        @media only screen and (min-width: 768px) {\r\n            >a {\r\n                >.post-title {\r\n                    font-size: 26px;\r\n                    line-height: 1.3;\r\n                    margin-bottom: 10px;\r\n                }\r\n                >.post-subtitle {\r\n                    font-size: 16px;\r\n                }\r\n                .post-meta {\r\n                    font-size: 12px;\r\n                }\r\n            }\r\n        }\r\n    }\r\n    .post-content-preview {\r\n        font-size: 14px;\r\n        font-style: italic;\r\n        color: #a3a3a3;\r\n        line-height: 1.8;\r\n        display: -webkit-box;\r\n        -webkit-box-orient: vertical;\r\n        -webkit-line-clamp: 3;\r\n        overflow: hidden;\r\n        @media only screen and (min-width: 768px) {\r\n            font-size: 14px;\r\n        }\r\n    }\r\n    /**\r\n    * 当前第几页\r\n    */\r\n    .page-index {\r\n        height: 50px;\r\n        line-height: 50px;\r\n        text-align: center;\r\n    }\r\n    .pager {\r\n        padding: 0px !important;\r\n        list-style: none;\r\n        >a,\r\n        >span {\r\n            text-transform: none;\r\n            text-decoration: none;\r\n            font-size: 13px;\r\n            font-weight: 800;\r\n            letter-spacing: 1px;\r\n            padding: 10px;\r\n            background-color: $white-color;\r\n            border-radius: 0;\r\n            color: lighten($text-color, 25%);\r\n            @media only screen and (min-width: 768px) {\r\n                font-size: 14px;\r\n                padding: 15px 25px;\r\n            }\r\n            &:hover,\r\n            &:focus {\r\n                color: $white-color;\r\n                background-color: $main-color;\r\n                ;\r\n                border: 1px solid $main-color;\r\n                >span {\r\n                    color: $white-color;\r\n                }\r\n            }\r\n        }\r\n        .disabled {\r\n            >a,\r\n            >a:hover,\r\n            >a:focus,\r\n            >span {\r\n                color: lighten($text-color, 50%);\r\n                background-color: lighten($text-color, 25%);\r\n                cursor: not-allowed;\r\n            }\r\n        }\r\n    }\r\n    .pager a {\r\n        border: 1px solid $ddd-color;\r\n        &.next {\r\n            float: right;\r\n        }\r\n        ;\r\n        &.previous {\r\n            float: left;\r\n        }\r\n        & {\r\n            >span {\r\n                font-weight: normal;\r\n                letter-spacing: 0.5px;\r\n            }\r\n        }\r\n    }\r\n}","/**\r\n* tags\r\n*/\r\n\r\n.site-tags {\r\n    .nav {\r\n        list-style: none;\r\n        padding: 0;\r\n        li {\r\n            display: inline-block;\r\n            font-size: 14px;\r\n            border: none;\r\n            line-height: 28px;\r\n            margin: 0 2px;\r\n            margin-bottom: 8px;\r\n            border-radius: 999em;\r\n            padding: 0 10px;\r\n            background-color: $main-color;\r\n            a {\r\n                color: $white-color;\r\n                text-decoration: none;\r\n            }\r\n            span {\r\n                display: inline-block;\r\n                min-width: 10px;\r\n                padding: 3px 7px;\r\n                font-size: 12px;\r\n                font-weight: 700;\r\n                line-height: 1;\r\n                background-color: $white-color;\r\n                color: $main-color;\r\n                border-radius: 10px;\r\n                text-align: center;\r\n            }\r\n        }\r\n    }\r\n}","/**\r\n * Page \r\n */\r\n\r\n.site-page {\r\n    color: $text-color;\r\n    .page-main {\r\n        margin-bottom: 10px;\r\n        margin-right: 20px;\r\n        padding: 10px 0 50px;\r\n        float: left;\r\n        width: 680px;\r\n        line-height: 1.5;\r\n        font-size: 16px;\r\n        color: $text-color;\r\n        .page-header {\r\n            padding-bottom: 20px;\r\n            .page-title {\r\n                @include relative-font-size(2.625);\r\n                letter-spacing: -1px;\r\n                line-height: 1;\r\n                @include media-query($on-laptop) {\r\n                    @include relative-font-size(2.25);\r\n                }\r\n            }\r\n        }\r\n        a {\r\n            color: $text-color;\r\n        }\r\n    }\r\n    .site-sidebar {\r\n        float: right;\r\n        width: 220px;\r\n    }\r\n    .not-sidebar {\r\n        width: 100%;\r\n        margin-right: 0;\r\n    }\r\n    @include media-query($on-laptop) {\r\n        .page-main {\r\n            width: 100%;\r\n            margin-right: 0;\r\n        }\r\n        .site-sidebar {\r\n            display: none;\r\n        }\r\n    }\r\n}","/**\r\n * Posts\r\n */\r\n\r\n.post {\r\n    col